customCSS:
/* Customize default CSS rules to get what you need
   or delete them all and add them to your global CSS */

.persoo-autocompleteDropdown__root {
  overflow-y: auto;
  max-height: 100%;
  width: 600px;
  border-radius: 0px;
  border: 1px solid #aaa;
  box-shadow: #666666 0px 0px 0px;
}
.persoo-autocompleteDataset__header,
.persoo-autocompleteDataset__footer,
.persoo-autocompleteDataset__hits__hit,
.persoo-autocompleteDataset__empty {
  box-sizing: border-box;
  width: 100%;
  padding: 10px 3% 10px 3%;
}
.persoo-autocompleteDataset__header {
  font-weight: bold;
}
.persoo-autocompleteDataset__hits__hit {
  cursor: pointer;
  line-height: 23px;
  overflow: hidden;
  font-size: 1.0em;
  text-align: left;
}
.persoo-autocompleteDataset__hits__hit,
.persoo-autocompleteDataset__hits__hit a,
.persoo-autocompleteDataset__hits__hit a:hover {
  text-decoration: none;
}

/* common hit styles */
.persoo-autocompleteDataset__hits__hit__title {
  padding-left: 5px;
}
.persoo-autocompleteDataset__hits__hit__price {
  font-weight: bold;
}

/* colors for dataset 1 */
.persoo-autocompleteDataset-1__root {
  background-color: #fff;
}
.persoo-autocompleteDataset-1__header {
  color: #333;
  background-color: #f0f0f0;
}
.persoo-autocompleteDataset-1__hits__hit.persoo-selected {
  background-color: #f0f0f0;
}
.persoo-autocompleteDataset-1__root,
.persoo-autocompleteDataset-1__hits a,
.persoo-autocompleteDataset-1__hits a:hover {
  color: #333;
}
.persoo-autocompleteDataset-1__hits__hit b {
  font-weight: bold;
  color: #498fdb;
}

/* colors for dataset 2 */
.persoo-autocompleteDataset-2__root {
  background-color: #224162;
}
.persoo-autocompleteDataset-2__header {
  color: #fff;
  background-color: #224162;
}
.persoo-autocompleteDataset-2__hits__hit.persoo-selected {
  background-color: #498fdb;
}
.persoo-autocompleteDataset-2__root,
.persoo-autocompleteDataset-2__hits a,
.persoo-autocompleteDataset-2__hits a:hover {
  color: #fff;
}
.persoo-autocompleteDataset-2__hits__hit b {
  font-weight: bold;
  color: #ddd;
}

/* layouts */
.persoo-autocompleteDropdown__root {
  display: -webkit-flex;
  display: flex;
}
.persoo-autocompleteDataset-2__root {
  -webkit-flex: 1;
  flex: 1;
}
.persoo-autocompleteDataset-1__root {
  -webkit-flex: 2;
  flex: 2;
}
.persoo-autocompleteDataset-1__hits {
  display: -webkit-flex;
  display: flex;
  -webkit-flex-wrap: wrap;
  flex-wrap: wrap;
}
.persoo-autocompleteDataset-1__hits__hit {
  box-sizing: content-box;
  -webkit-flex: 1;
  flex: 1;
  width: auto;
  min-width: 100px;
  max-width: 150px;
  min-height: 160px;
}
.persoo-autocompleteDataset-1__hits__hit a {
  display: -webkit-flex;
  display: flex;
  -webkit-flex-direction: column;
  flex-direction: column;
}
.persoo-autocompleteDataset-1__hits__hit a div {
  vertical-align: middle;
  text-align: center;
}
.persoo-autocompleteDataset__hits__hit img {
  max-width: 100px;
  max-height: 100px;
}
